DROP TABLE IF EXISTS tpp_event;
CREATE TABLE `tpp_event` (
  `id` int(11) NOT NULL auto_increment,
  `dd_begin` int(11) NOT NULL default '1',
  `mm_begin` int(11) NOT NULL default '1',
  `yy_begin` int(11) NOT NULL default '0',
  `dd_end` int(11) NOT NULL default '1',
  `mm_end` int(11) NOT NULL default '1',
  `yy_end` int(11) NOT NULL default '0',
  `name` varchar(50) NOT NULL default 'нет',
  `anons` varchar(200) NOT NULL default 'нет',
  `page` text NOT NULL,
  `vidimost` int(11) NOT NULL default '1',
  `type_id` int(11) NOT NULL default '0',
  `kls_id` int(11) NOT NULL default '0',
  PRIMARY KEY  (`id`)
);


DROP TABLE IF EXISTS tpp_geoobject;
CREATE TABLE `tpp_geoobject` (
  `id` int(11) NOT NULL auto_increment,
  `kls_id` int(11) NOT NULL default '0',
  `name` varchar(50) NOT NULL default 'нет',
  `coord` text NOT NULL,
  `page` text NOT NULL,
  `dd_c` datetime NOT NULL default '0000-00-00 00:00:00',
  `user_c` varchar(50) NOT NULL default 'нет',
  `dd_u` datetime NOT NULL default '0000-00-00 00:00:00',
  `user_u` varchar(50) NOT NULL default 'нет',
  `vidimost` int(11) NOT NULL default '1',
  `event_id` int(11) NOT NULL default '0',
  `ud` int(11) NOT NULL default '0',
  `kart` text NOT NULL,
  `kolvo` int(11) NOT NULL default '1',
  `need_def` int(11) NOT NULL default '-1',
  `need_mon` int(11) NOT NULL default '-1',
  PRIMARY KEY  (`id`)
);


DROP TABLE IF EXISTS tpp_kls;
CREATE TABLE `tpp_kls` (
  `id` int(11) NOT NULL auto_increment,
  `lv` int(11) NOT NULL default '0',
  `pn` int(11) NOT NULL default '1',
  `parent_id` int(11) NOT NULL default '0',
  `name` varchar(50) NOT NULL default 'нет',
  `lname` varchar(50) NOT NULL default 'нет',
  `icon` varchar(50) NOT NULL default 'нет',
  `pos_left` int(11) NOT NULL default '0',
  `pos_top` int(11) NOT NULL default '0',
  `pattern` text NOT NULL,
  `page` text NOT NULL,
  `vidimost` tinyint(4) NOT NULL default '1',
  `tip` int(11) NOT NULL default '0',
  `icon_width` int(11) NOT NULL default '30',
  `icon_height` int(11) NOT NULL default '60',
  PRIMARY KEY  (`id`)
);


DROP TABLE IF EXISTS tpp_page;
CREATE TABLE `tpp_page` (
  `id` int(11) NOT NULL auto_increment,
  `lv` int(11) NOT NULL default '0',
  `pn` int(11) NOT NULL default '1',
  `name` varchar(50) NOT NULL default 'нет',
  `full_name` varchar(250) NOT NULL default 'нет',
  `page` text NOT NULL,
  `page_add` text NOT NULL,
  `vidimost` tinyint(4) NOT NULL default '1',
  PRIMARY KEY  (`id`)
);


DROP TABLE IF EXISTS tpp_type_event;
CREATE TABLE `tpp_type_event` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(50) NOT NULL default 'нет',
  `page` text NOT NULL,
  `pn` int(11) NOT NULL default '0',
  PRIMARY KEY  (`id`)
);


DROP TABLE IF EXISTS tpp_users;
CREATE TABLE `tpp_users` (
  `id` int(11) NOT NULL auto_increment,
  `login` text,
  `psw` text,
  `TYPE` int(11) default NULL,
  PRIMARY KEY  (`id`)
);


