INSERT INTO `tpp_page` VALUES
(1, 1, 1, 'Рейтинг экологов', 'Рейтинг экологической активности', '<p>за февраль месяц 2015</p>\r\n<p>&nbsp;</p>\r\n<table class=\"rules\" border=\"1\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr bgcolor=\"#eeeeee\">\r\n<td>Место</td>\r\n<td>Активист</td>\r\n<td>Сумма баллов</td>\r\n</tr>\r\n<tr>\r\n<td>1</td>\r\n<td>Иван Огородников</td>\r\n<td align=\"center\">51</td>\r\n</tr>\r\n<tr>\r\n<td>2</td>\r\n<td>Мария Клумбовна</td>\r\n<td align=\"center\">46</td>\r\n</tr>\r\n<tr>\r\n<td>3</td>\r\n<td>Катя Саженцева</td>\r\n<td align=\"center\">31</td>\r\n</tr>\r\n<tr>\r\n<td>4</td>\r\n<td>Петя Соколов</td>\r\n<td align=\"center\">29</td>\r\n</tr>\r\n<tr>\r\n<td>5</td>\r\n<td>Елена Плешкова</td>\r\n<td align=\"center\">27</td>\r\n</tr>\r\n<tr style=\"background-color: #ffd878;\">\r\n<td>6</td>\r\n<td>Надежда Баглей</td>\r\n<td align=\"center\">21</td>\r\n</tr>\r\n<tr>\r\n<td>7</td>\r\n<td>Денис Галицкий</td>\r\n<td align=\"center\">17</td>\r\n</tr>\r\n<tr>\r\n<td>8</td>\r\n<td>Владимир Кошин</td>\r\n<td align=\"center\">молодец!</td>\r\n</tr>\r\n<tr>\r\n<td>9</td>\r\n<td>Екатерина Нелюбина</td>\r\n<td align=\"center\">14</td>\r\n</tr>\r\n<tr>\r\n<td>10</td>\r\n<td>Екатерина Крайнева</td>\r\n<td align=\"center\">7</td>\r\n</tr>\r\n</tbody>\r\n</table>', '<p>Текст 1 (дополнительный)</p>', 1),
(2, 1, 3, 'Правила системы', 'Правила', '<p>Наш город Пермь считается одним из самых зеленых городов России. Для того, чтобы сохранять и управлять        этим богатством, нужно знать, где и какие растения растут и в каком состоянии они находятся.</p>\r\n<p>Эта информация позволит правильно организовать уход за растениями со стороны администрации и со      стороны жителей города, остановить незаконные рубки,  провести посадки в тех местах, где их недостаточно.</p>\r\n<p>Мы приглашаем каждого жителя нашего города помочь в сборе этой важной информации - проявить экологическую активность.</p>\r\n<p>Экологическая активность участников приветствуется и поощряется!</p>\r\n<p>Начисление экологических баллов производится по следующим правилам:</p>\r\n<table class=\"rules\" border=\"0\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td>нанесен новый природный объект на карту (дерево, скворечник, маршрут)</td>\r\n<th>2</th>\r\n</tr>\r\n<tr>\r\n<td>подтвердить существование природного объекта</td>\r\n<th>1</th>\r\n</tr>\r\n<tr>\r\n<td>нанесен некорректный объект</td>\r\n<th>-1</th>\r\n</tr>\r\n<tr>\r\n<td>посажено дерево-куст</td>\r\n<th>30</th>\r\n</tr>\r\n<tr>\r\n<td>установлен домик для птиц или белок</td>\r\n<th>50</th>\r\n</tr>\r\n<tr>\r\n<td>установлена кормушка и ведется подкормка птиц</td>\r\n<th>20</th>\r\n</tr>\r\n<tr>\r\n<td>организован экологический праздник(посадка деревьев, уборка леса, проведение урока, пр.)</td>\r\n<th>100<br /><br /></th>\r\n</tr>\r\n</tbody>\r\n</table>', '<p>Дополнительно к правилам</p>', 1),
(3, 1, 4, 'Организаторы проекта', 'Организаторы', '<h1 style=\"margin-bottom: 12px;\"><strong style=\"font-size: 10px;\">Фонд культурного и природного наследия \"Обвинская роза\"</strong></h1>\r\n<p style=\"margin-bottom: 12px;\">тел. 293-92-19, <a href=\"mailto:artis.e@yandex.ru\">artis.e@yandex.ru</a></p>\r\n<p style=\"margin-bottom: 12px;\"><strong>Социальный проект \"Сад соловьев у речки Уинки\"</strong><br /> <a href=\"http://www.uinka.ru\">www.uinka.ru</a>, <a href=\"http://vk.com/uinka\">www.vk.com/uinka</a></p>\r\n<p style=\"margin-bottom: 12px;\"><strong>Студия дизайна \"Жанр\" </strong><br /> <a href=\"http://janr.perm.ru\">www.ЖАНР.рф</a>, <a href=\"mailto:janr@pochta.ru\">janr@pochta.ru</a></p>\r\n<p style=\"margin-bottom: 12px;\">Сделано во время хакатона при содействии \"Теплицы социальных технологий\" <a href=\"http://www.te-st.ru\">www.te-st.ru</a></p>\r\n<p style=\"margin-bottom: 12px;\">Руки приложили:</p>\r\n<p>- Елена Плешкова</p>\r\n<p>- Надежда Баглей</p>\r\n<p>- Денис Галицкий</p>\r\n<p>- Владимир Кошин</p>\r\n<p>- Екатерина Нелюбина</p>\r\n<p>- Екатерина Крайнева</p>', '<p>Доп.инфо</p>', 1),
